         */
        formatGeocodingResultFunc = function (result) {
            if (!result) {
                return {};
            }
            var data = {}, geometry = result.geometry, viewport = geometry.viewport, bounds = geometry.bounds;

            var level = {
                'country' : false,
                'region': false,
                'departement': false,
                'city': false
            };

            var name2level = [
                ['country', 'country'],
                ['administrative_area_level_1', 'region'],
                ['administrative_area_level_2', 'departement'],
                ['locality', 'city'],
            ]

            data[locale] = {};
                        $.each(result.address_components, function (index, object) {
                //var name = object.types[0];
                $.each(name2level, (idx, it) => {
                    if (it[0] == object.types[0]) {
                        level[it[1]] = true;
                    }
                });
                if ($.inArray('country', object.types));
                $.each(object.types, function (index, name) {
                    data[locale][name] = object.long_name;
                    data[locale][name + "_short"] = object.short_name;
                });
            });

            $.extend(data, {
                formatted_address: result.formatted_address,
                types: result.types,
                location_type: geometry.location_type || "PLACES",
                viewport: viewport,
                bounds: bounds,
                location: geometry.location,
                lat: geometry.location.lat(),
                lng: geometry.location.lng(),
                levels : level,
            });

            return data;
        };

        /**
