         */
        initializeGMapFunc = function () {
            geocoder = new google.maps.Geocoder();

            if (mapElt) {
                latlng = new google.maps.LatLng(defaultLat, defaultLng);
                var mapOptions = {
                    zoom: defaultZoom,
