        extendSearchLocationFunc = function (parentLocationAddress, parentLocationType, modalId, modalMessage) {
            if (autoCompleteElt && parentLocationAddress && parentLocationType) {
                var modal = $(modalId);
                if (modal.length) {
                    modal.find('.modal-body .title').html(modalMessage);
