                        try {
                            if (e.type == 'mouseenter') {
                                marker.labelClass = "map-label-overlay";
                                if (marker.cluster) {//setted in clusteringend event below
                                    marker.cluster.clusterIcon_.div_.className = 'cluster cluster-overlay';
                                }
