        $(function () {

            // Set default favourites in the listing
            common.application.setDefaultFavourites();

            common.application.initMultiSelect(
                    '#categories',
                    'Toutes',
                    'Type\u0020de\u0020prestation',
                    'selectionn\u00E9es'
            );
        });
    </script>

